import UIKit
import RxSwift
import FlexLayout
import PinLayout

protocol ProductDetailDisplayLogic: AnyObject {
    func displayItemDetailInfo(
        productInfoViewModel: ProductDetail.DetailProductItem.ProductDetailInfoViewModel,
        productImageListViewModel: [ProductDetail.DetailProductItem.ProductDetailImageViewModel]
    )
}

class ProductDetailViewController: UIViewController, ProductDetailDisplayLogic {
    var interactor: ProductDetailBusinessLogic?
    var router: (NSObjectProtocol & ProductDetailRoutingLogic & ProductDetailDataPassing)?
    
    // MARK: Object lifecycle
    private var rootFlexContainer: UIView = UIView()
    private var productImageListView: ProductDetailImageListView = ProductDetailImageListView()
    
    override init(nibName nibNameOrNil: String?, bundle nibBundleOrNil: Bundle?) {
        super.init(nibName: nibNameOrNil, bundle: nibBundleOrNil)
        setup()
    }
    
    required init?(coder aDecoder: NSCoder) {
        super.init(coder: aDecoder)
        setup()
    }
    
    // MARK: Setup
    
    private func setup() {
        let viewController = self
        let interactor = ProductDetailInteractor()
        let presenter = ProductDetailPresenter()
        let router = ProductDetailRouter()
        viewController.interactor = interactor
        viewController.router = router
        interactor.presenter = presenter
        presenter.viewController = viewController
        router.viewController = viewController
        router.dataStore = interactor
    }
    
    // MARK: Routing
    
    
    // MARK: View lifecycle
    
    override func viewDidLoad() {
        super.viewDidLoad()
        setupViews()
        interactor?.fetchDetailInfo()
    }
    
    private func setupViews() {
        view.backgroundColor = .white
        
        view.addSubview(rootFlexContainer)
        rootFlexContainer.flex
            .direction(.column)
            .define { rootFlex in
                rootFlex
                    .addItem(productImageListView)
                    .width(100%)
                    .height(100%)
            }
    }
    
    override func viewDidLayoutSubviews() {
        super.viewDidLayoutSubviews()
        
        rootFlexContainer.pin
            .horizontally()
            .top()
            .bottom(view.pin.safeArea.bottom)
        rootFlexContainer.flex.layout()
    }
    
    // MARK: Do something
    func displayItemDetailInfo(
        productInfoViewModel: ProductDetail.DetailProductItem.ProductDetailInfoViewModel,
        productImageListViewModel: [ProductDetail.DetailProductItem.ProductDetailImageViewModel]
    ) {
        self.productImageListView.updateViewModels(productImageListViewModel)
    }
}
